mapper.select:
/* 게시판 총 개수 조회 - BoarderMapper.pagingCountBySearch */
SELECT COUNT(*)
FROM BOARD BD

/*게시판 페이징 조회 - BoarderMapper.pagingBySearch */ SELECT
,MAX(ENTITY.REG_DTTM) AS REG_DTTM , REG_ID FROM BOARD BD INNER JOIN ENTITY_LOG ENTITY ON BD.BOARD_SEQ = ENTITY.LOG_ID1
AND ENTITY.ENTITY_NAME = #{entityName}
AND BD.TITLE LIKE CONCAT('%', #{searchWord1}, '%') AND BD.CONTENTS LIKE CONCAT('%', #{searchWord1}, '%') AND ENTITY.REG_ID LIKE CONCAT('%', #{searchWord1}, '%') AND ( BD.TITLE LIKE CONCAT('%', #{searchWord1}, '%') OR BD.CONTENTS LIKE CONCAT('%', #{searchWord1}, '%') OR ENTITY.REG_ID LIKE CONCAT('%', #{searchWord1}, '%'))
GROUP BY BOARD_SEQ
LIMIT #{paging.offset} , #{paging.displayRow}

/*게시판 단 건 조회 - BoarderMapper.pagingBySearch */ SELECT
,MAX(ENTITY.REG_DTTM) AS REG_DTTM , REG_ID FROM BOARD BD INNER JOIN ENTITY_LOG ENTITY ON BD.BOARD_SEQ = ENTITY.LOG_ID1
AND ENTITY.ENTITY_NAME = #{entityName}
GROUP BY BOARD_SEQ

/*게시판 목록 갯수 조회 - BoarderMapper.findAllTotalCount */
  SELECT COUNT(1)
    FROM BOARD_PUBLIC BP

/*게시판 목록 조회 - BoarderMapper.findAll */
  SELECT 
    FROM BOARD_PUBLIC BP



GROUP BY BOARD_SEQ


    LIMIT #{paging.displayRow} OFFSET #{paging.offset}

/*게시판 댓글 조회 - BoarderMapper.findReplyBySeq */
  SELECT 
    FROM BOARD_RIPPLE BP
   WHERE BOARD_SEQ = #{boardSeq}
     AND DEL_YN = 'N'
ORDER BY RIPPLE_NO DESC

mapper.delete:
/* 게시판 단 건 삭제 - BoardMapper.delete */
DELETE FROM BOARD
WHERE
    BOARD_SEQ = #{boardSeq}

mapper.update:
/* 게시판 다 건 삭제 - BoardMapper.deleteList */
UPDATE BOARD_PUBLIC
   SET DEL_YN = 'Y', LAST_DT = NOW(), LAST_ID = #{lastId}
 WHERE BOARD_SEQ IN

    #{seq}

/* 게시판 수정 - BoardMapper.updateBoard */
UPDATE BOARD_PUBLIC
   SET TITLE = #{title}, CONTENTS = #{contents}, LAST_DT = NOW(), LAST_ID = #{lastId}
 WHERE BOARD_SEQ = #{boardSeq}

/* 게시판 조회수 증가 - BoardMapper.increaseHits */
UPDATE BOARD_PUBLIC
   SET HITS = HITS + 1
 WHERE BOARD_SEQ = #{boardSeq}

/* 게시판 댓글 삭제 - BoardMapper.removeReplyBySeq */
UPDATE BOARD_RIPPLE
   SET DEL_YN = 'Y'
 WHERE RIPPLE_SEQ = #{rippleSeq}
   AND BOARD_SEQ = #{boardSeq}
